import React, { useState } from "react";
import { Button, TextField } from "@mui/material";
import { loginButtonStyle, parentDivStyle } from "./Login.styles";
import { useNavigate } from "react-router-dom";
import axios from 'axios';

export const Login = (): JSX.Element => {
    const [email, setEmail] = useState<string>("");
    const [password, setPassword] = useState<string>("");
    const navigate = useNavigate();

    const onChangeEmail = (event: React.ChangeEvent<HTMLInputElement>): void => {
        setEmail(event.target.value);
    };

    const onChangePassword = (event: React.ChangeEvent<HTMLInputElement>): void => {
        setPassword(event.target.value);
    };

    const login = (event: any): void => {
        try {
            // Make a POST request to your Spring Boot login endpoint
            const response =  axios.post('http://localhost:8080/User/Login', {
                email: email,
                password: password,
            });

            // Handle the response, e.g., store user information in state or redirect to another page
            console.log('Login successful', response.data);
            // Assuming you want to redirect after successful login
            navigate('/Home');
        } catch (error) {
            // Handle login error, e.g., display an error message
            console.error('Login failed', (error as Error).message);
        }
    };

    return (
        <div
            style={{
                ...parentDivStyle,
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                height: "100vh",

            }}
        >
            <div
                style={{
                    width: "300px",
                    padding: "20px",
                    border: "1px solid #ccc",
                    borderRadius: "8px",
                    backgroundColor: "rgba(255, 255, 255, 0.8)" // You can adjust the background color opacity
                }}
            >
                <div style={{ marginBottom: 20 }}>
                    <TextField
                        id="email"
                        label="Email"
                        variant="outlined"
                        fullWidth
                        value={email}
                        onChange={onChangeEmail}
                    />
                </div>
                <div style={{ marginBottom: 20 }}>
                    <TextField
                        id="password"
                        label="Password"
                        type="password"
                        variant="outlined"
                        fullWidth
                        value={password}
                        onChange={onChangePassword}
                    />
                </div>
                <Button
                    style={loginButtonStyle}
                    onClick={login}
                    variant="contained"
                    color="secondary"
                    fullWidth
                >
                    Login
                </Button>
                <img src="/happyplanner.jpg" alt="Logo"  />
            </div>
        </div>
    );
};

export default Login;
