import React, { useState, useEffect } from "react";
import {Button, TextField, List, ListItem, ListItemText, IconButton, 
    Table, TableBody, TableCell, TableContainer, TableHead, TableRow,} from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";
import EditIcon from "@mui/icons-material/Edit";
import AddIcon from "@mui/icons-material/Add";
import SaveIcon from "@mui/icons-material/Save";
import axios from 'axios';

interface Category {
  categoryId: number;
  name: string;
}

interface Task {
  taskId: number;
  priority: number;
  title: string;
  description: string;
  status: string;
  dueDate: Date;
}

interface User {
  id: number;
  username: string;
  email:string;
  password:string;
  admin:Boolean;
  // other user properties...
}

interface Reminder {
  reminderId: number;
  userId: number;
  taskId: number;
  // other reminder properties...
}

const AdminPage = (): JSX.Element => {
  const [categories, setCategories] = useState<Category[]>([]);
  const [tasks, setTasks] = useState<Task[]>([]);
  const [users, setUsers] = useState<User[]>([]);
  const [reminders, setReminders] = useState<Reminder[]>([]);
  const [newCategory, setNewCategory] = useState<string>("");
  const [newTask, setNewTask] = useState<string>("");
  const [newUser, setNewUser] = useState<string>("");
  const [newReminder, setNewReminder] = useState<string>("");
  const [selectedUser, setSelectedUser] = useState<User | null>(null);
  const [editedCategoryId, setEditedCategoryId] = useState<number | null>(null);
  const [editedCategoryName, setEditedCategoryName] = useState<string>("");

  const ReadAllUsers=(): void =>{

    try {
        axios.get(`http://localhost:8081/User/ReadAll`, {
        }).then(response=> { 
        localStorage.setItem("userii", JSON.stringify(response.data));
        setUsers(response.data);
        console.log('Import succesful', response.data);
        
    });       
  
    } catch (error) {
        console.error('Import failed', (error as Error).message);
    }
   

  }

  const ReadAllCategories = (): void => {
    //setSelectedUser(user);
    try {
       axios.get(`http://localhost:8081/Category/ReadAll`, {
       }).then(response=> { 
       localStorage.setItem("categorii", JSON.stringify(response.data));
       setCategories(response.data);
       console.log('Import succesful', response.data);
       
       // const approachingDate = new Date(taskDue.getTime() - 24 * 60 * 60 * 1000); // 1 day before
       // const currentDate = new Date();
 
       // if (currentDate < approachingDate) {
   });       
 
   } catch (error) {
       console.error('Import failed', (error as Error).message);
   }
  
 };

  const handleUserClick = (user: User | null) => {
    setSelectedUser(user);
  };

  const handleAddCategory = () => {
    // Implement logic for adding a new category
  };

  const handleAddTask = () => {
    // Implement logic for adding a new task
  };

  const handleAddUser = () => {
    // Implement logic for adding a new user
  };

  const handleAddReminder = () => {
    // Implement logic for adding a new reminder
  };

  const handleDeleteCategory = (categoryId: number) => {
    // Implement logic for deleting a category
  };

  const handleDeleteTask = (taskId: number) => {
    // Implement logic for deleting a task
  };

  const handleDeleteUser = (userId: number) => {
    // Implement logic for deleting a user
  };

  const handleDeleteReminder = (reminderId: number) => {
    // Implement logic for deleting a reminder
  };

  const handleEditCategory = (categoryId: number) => {
    const categoryToEdit = categories.find((category) => category.categoryId === categoryId);

    if (categoryToEdit) {
      setEditedCategoryId(categoryId);
      setEditedCategoryName(categoryToEdit.name);}
  };

  const handleEditTask = (taskId: number) => {
    // Implement logic for editing a task
  };

  const handleEditUser = (userId: number) => {
    // Implement logic for editing a user
  };

  const handleEditReminder = (reminderId: number) => {
    // Implement logic for editing a reminder
  };

  useEffect(() => {
      
  }, [selectedUser]);

  const renderTables = (selectedUser: User | null) => { 
    if (!selectedUser) {
      return <p>Please select a user from the list.</p>;
    }
    return (
        <div>
            
         {/* Categories Table */}
     
    {/* Tasks Table */}
    <TableContainer>
      {/* Similar structure for Tasks Table */}
    </TableContainer>


    {/* Reminders Table */}
    <TableContainer>
      {/* Similar structure for Reminders Table */}
    </TableContainer>

    
      {/* Add New Category */}
      <TextField
        label="New Category"
        variant="outlined"
        value={newCategory}
        onChange={(e) => setNewCategory(e.target.value)}
      />
      <Button onClick={handleAddCategory} startIcon={<AddIcon />}>
        Add Category
      </Button>

      {/* Add New Task */}
      {/* Similar structure for Add New Task */}

      

      {/* Add New Reminder */}
      {/* Similar structure for Add New Reminder */}
    </div>
    );
    
};

  return (
    <div>
      <h2>Admin Page</h2>
       
       
      <List style={{overflow: "auto"}}>
        <ListItem
          button
          selected={!selectedUser}
          onClick={() => ReadAllUsers()}
        >
          <ListItemText primary="All Users" />
          </ListItem>

          <TableContainer>
      <Table>
        <TableHead>
          <TableRow>
            <TableCell>UserID</TableCell>
            <TableCell>email</TableCell>
            <TableCell>username</TableCell>
            <TableCell>password</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {users.map((user) => (
            <TableRow key={user.id}>
              <TableCell>{user.id}</TableCell>
              <TableCell>{user.username}</TableCell>  
              <TableCell>{user.email}</TableCell>
              <TableCell>{user.password}</TableCell>
              <TableCell>
                <IconButton onClick={() => handleEditUser(user.id)}>
                  <EditIcon />
                </IconButton>
                <IconButton onClick={() => handleDeleteUser(user.id)}>
                  <DeleteIcon />
                </IconButton>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>    
    <ListItem
          button
          selected={!selectedUser}
          onClick={() => ReadAllCategories()}
        >
          <ListItemText primary="All Categories" />
          </ListItem>
    <TableContainer>
      <Table>
        <TableHead>
          <TableRow>
            <TableCell>Category ID</TableCell>
            <TableCell>Name</TableCell>
            <TableCell>Actions</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {categories.map((category) => (
            <TableRow key={category.categoryId}>
             // <TableCell>{category.categoryId}</TableCell>
              <TableCell>
                  {editedCategoryId === category.categoryId ? (
                    <TextField
                      value={editedCategoryName}
                      onChange={(e) => setEditedCategoryName(e.target.value)}
                    />
                  ) : (
                    category.name
                  )}
                </TableCell>
              <TableCell>{category.name}</TableCell>
              <TableCell>
                <IconButton onClick={() => handleEditCategory(category.categoryId)}>
                  <EditIcon />
                </IconButton>
                <IconButton onClick={() => handleDeleteCategory(category.categoryId)}>
                  <DeleteIcon />
                </IconButton>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>

     
      </List>
    </div>
  );
};

export default AdminPage;
