import React, { useState } from "react";
import { Button, TextField, List, ListItem, ListItemText } from "@mui/material";
import { loginButtonStyle, parentDivStyle } from "./Login.styles";

const Tasks = (): JSX.Element => {
  const [selectedCategory, setSelectedCategory] = useState<string | null>(null);
  const [categories, setCategories] = useState<string[]>(["Category 1", "Category 2", "Category 3"]);
  const [newCategory, setNewCategory] = useState<string>("");
  const [tasks, setTasks] = useState<string[]>(["Task 1", "Task 2", "Task 3"]);
  const [newTask, setNewTask] = useState<string>("");

  const ReadAll = (category: string) => {
    // try {
    //     axios.post('http://localhost:8081/Task/ReadAll', {
          
    //         setCategories: categories,
    //         password: password
            
    //     }).then(response=> {    localStorage.setItem("userul", JSON.stringify(response.data));
    //     console.log('Login successful', response.data);
    //     navigate('/Home');
    // });       

    // } catch (error) {
    //     console.error('Login failed', (error as Error).message);
    // }
    setSelectedCategory(category);
  };

  const handleAddCategory = () => {
    if (newCategory.trim() !== "") {
      setCategories([...categories, newCategory]);
      setNewCategory("");
    }
  };

  const handleAddTask = () => {
    if (newTask.trim() !== "") {
      setTasks([...tasks, newTask]);
      setNewTask("");
    }
  };

  return (
    <div style={{ display: "flex" }}>
         <img
                style={{
                    position: "absolute",
                    top: 0,
                    left: 0,
                    width: "100%",
                    height: "100%",
                    objectFit: "fill",  
                    opacity: 1,  
                    zIndex:-3
                }}
                src={require('../images/jpeg.jpeg')} 
                alt="Background"
            />
      
      <div style={{marginTop:30, padding: "60px", overflow: "scroll" }}>
        <List>
          { categories.map((category) => (
            <ListItem
              key={category}
              button
              selected={selectedCategory === category}
              onClick={() => ReadAll(category)}
            >
              <ListItemText primary={category} />
            </ListItem>
          ))}
        </List>
        <TextField
          label="New Category"
          variant="outlined"
          value={newCategory}
          onChange={(e) => setNewCategory(e.target.value)}
        />
        <Button variant="contained" color="primary" onClick={handleAddCategory}>
          Add Category
        </Button>
      </div>

      {/* Right side - Tasks for the selected category */}
      <div style={{flex: 1, padding: "40px"}}>
        <h2>Tasks for {selectedCategory || "All Categories"}</h2>
        <ul>
          {tasks.map((task, index) => (
            <li key={index}>{task}</li>
          ))}
        </ul>
        <TextField
          label="New Task"
          variant="outlined"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
        />
        <Button variant="contained" color="primary" onClick={handleAddTask}>
          Add Task
        </Button>
      </div>
    </div>
  );
};

export default Tasks;
